set stu_id = #{stuId,jdbcType=VARCHAR},
  eva_id = #{evaId,jdbcType=VARCHAR},
  es_id = #{esId,jdbcType=VARCHAR},
  grade = #{grade,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, stu_id, eva_id, es_id, grade
from evaresult
where id = #{id,jdbcType=INTEGER}

